import React from 'react';
import { Box, type CommonProps, type ExpandProps } from '@contentful/f36-core';

import { getAccordionStyles } from './Accordion.styles';

export interface AccordionProps extends CommonProps {
  /**
   * Specify the alignment of the chevron inside the accordion header
   * @default end
   */
  align?: 'start' | 'end';
  /**
   * Child nodes to be rendered in the component
   */
  children?: React.ReactNode;
}

const _Accordion = (
  {
    align = 'end',
    children,
    className,
    testId = 'cf-ui-accordion',
    ...otherProps
  }: ExpandProps<AccordionProps>,
  ref: React.Ref<HTMLUListElement>,
) => {
  const styles = getAccordionStyles({ className });
  return (
    <Box
      as="ul"
      className={styles.accordion}
      testId={testId}
      {...otherProps}
      ref={ref}
    >
      {React.Children.map(children, (child) => {
        if (React.isValidElement(child)) {
          return React.cloneElement(child, {
            align,
          });
        }
        return child;
      })}
    </Box>
  );
};

export const Accordion = React.forwardRef(_Accordion);
import { css, cx } from 'emotion';
import tokens from '@contentful/f36-tokens';

export const getAccordionStyles = ({ className }: { className?: string }) => ({
  accordion: cx(
    css({
      boxSizing: 'border-box',
      padding: '0',
      margin: '0',
      listStyle: 'none',
      '&:first-child': {
        borderTop: `1px solid ${tokens.gray300}`,
      },
    }),
    className,
  ),
});
import React from 'react';
import type { HeadingElement } from '@contentful/f36-typography';
import {
  useId,
  Box,
  useControllableState,
  type CommonProps,
  type ExpandProps,
} from '@contentful/f36-core';

import { AccordionHeader } from '../AccordionHeader/AccordionHeader';
import { AccordionPanel } from '../AccordionPanel/AccordionPanel';
import { getAccordionItemStyles } from './AccordionItem.styles';

export interface AccordionItemProps extends CommonProps {
  /**
   * The accordion title
   */
  title?: React.ReactNode;
  /**
   * The heading element that will be used by the AccordionHeader
   */
  titleElement?: HeadingElement;
  /**
   * The children of the AccordionItem are in fact the content of the accordion
   */
  children?: React.ReactNode;
  /**
   * A function to be called when the accordion item is opened
   */
  onExpand?: () => void;
  /**
   * A function to be called when the accordion item is closed
   */
  onCollapse?: () => void;
  /**
   * Specify the alignment of the chevron inside the accordion header
   */
  align?: 'start' | 'end';

  /**
   * By default, the AccordionItem is uncontrolled (manage it's expanded state by itself)
   * But you can make it controlled by providing boolean
   */
  isExpanded?: boolean;
}

const _AccordionItem = (
  {
    title = 'Accordion Title',
    titleElement = 'h2',
    testId = 'cf-ui-accordion-item',
    onExpand,
    onCollapse,
    children,
    align = 'end',
    className,
    isExpanded,
    ...otherProps
  }: ExpandProps<AccordionItemProps>,
  ref: React.Ref<HTMLLIElement>,
) => {
  const styles = getAccordionItemStyles({ className });
  const id = useId();
  const { isOpen, handleOpen, handleClose } = useControllableState({
    isOpen: isExpanded,
    onOpen: onExpand,
    onClose: onCollapse,
  });

  const handleOnClick = () => {
    if (isOpen) {
      handleClose();
    } else {
      handleOpen();
    }
  };

  return (
    <Box
      as="li"
      className={styles.accordionItem}
      testId={`${testId}-${id}`}
      {...otherProps}
      ref={ref}
    >
      <AccordionHeader
        onClick={handleOnClick}
        isExpanded={isOpen}
        element={titleElement}
        ariaId={id}
        align={align}
      >
        {title}
      </AccordionHeader>

      <AccordionPanel ariaId={id} isExpanded={isOpen}>
        {children}
      </AccordionPanel>
    </Box>
  );
};

export const AccordionItem = React.forwardRef(_AccordionItem);
import React from 'react';
import { Subheading, type HeadingElement } from '@contentful/f36-typography';
import { ChevronDownTrimmedIcon } from '@contentful/f36-icons';
import type { CommonProps } from '@contentful/f36-core';

import { getAccordionHeaderStyles } from './AccordionHeader.styles';

export interface AccordionHeaderProps extends CommonProps {
  /**
   * Child nodes to be rendered in the component
   */
  children?: React.ReactNode;
  /**
   * The function that will be called once the user clicks on the accordion title
   */
  onClick: VoidFunction;
  /**
   * A boolean that tells if the accordion should be expanded or collapsed
   */
  isExpanded: boolean;
  /**
   * An unique id that is necessary for the aria roles and properties
   */
  ariaId: string;
  /**
   * The heading element that will be used by the Subheading component
   */
  element?: HeadingElement;
  /**
   * Specify the alignment of the chevron inside the accordion header
   */
  align?: 'start' | 'end';
}

export const AccordionHeader = ({
  children,
  onClick,
  isExpanded = false,
  ariaId,
  element = 'h2',
  align = 'end',
  testId = 'cf-ui-accordion-header',
  ...rest
}: AccordionHeaderProps) => {
  const styles = getAccordionHeaderStyles({ align, isExpanded });

  return (
    <Subheading {...rest} testId={testId} marginBottom="none" as={element}>
      <button
        type="button"
        aria-expanded={isExpanded}
        aria-controls={`accordion-panel--${ariaId}`}
        id={`accordion--${ariaId}`}
        className={styles.accordionHeader}
        onClick={onClick}
      >
        <ChevronDownTrimmedIcon
          className={styles.accordionHeaderIcon}
          variant="secondary"
        />
        {children}
      </button>
    </Subheading>
  );
};
import { css, cx } from 'emotion';
import tokens from '@contentful/f36-tokens';

type StyleProps = {
  align: 'start' | 'end';
  isExpanded: boolean;
};

const getHeaderStyles = ({ align }: StyleProps) =>
  cx(
    css({
      display: 'flex',
      flexDirection: 'row',
      alignItems: 'center',
      border: '0',
      margin: 0, // remove the default button margin in Safari.
      padding: tokens.spacingM,
      backgroundColor: 'transparent',
      fontFamily: tokens.fontStackPrimary,
      fontSize: tokens.fontSizeL,
      fontWeight: tokens.fontWeightDemiBold,
      lineHeight: tokens.lineHeightL,
      color: tokens.gray800,
      width: '100%',
      minWidth: '9px',
      cursor: 'pointer',
      transition: `background-color ${tokens.transitionDurationDefault} ${tokens.transitionEasingDefault},
        box-shadow ${tokens.transitionDurationShort} ${tokens.transitionEasingDefault}`,
      '&:hover': {
        backgroundColor: tokens.gray100,
      },
      '&:focus': {
        backgroundColor: tokens.gray100,
        borderRadius: tokens.borderRadiusMedium,
        boxShadow: tokens.glowPrimary,
        outline: 'none',
      },
      '&:focus:not(:focus-visible)': {
        backgroundColor: 'transparent',
        borderRadius: 'unset',
        boxShadow: 'unset',
      },
      '&:focus-visible': {
        backgroundColor: tokens.gray100,
        borderRadius: tokens.borderRadiusMedium,
        boxShadow: tokens.glowPrimary,
      },
    }),
    align === 'end' &&
      css({
        flexDirection: 'row-reverse',
        justifyContent: 'space-between',
      }),
  );

const getIconStyles = ({ align, isExpanded }: StyleProps) =>
  cx(
    css({
      transform: 'rotate(0deg)',
      transition: `transform ${tokens.transitionDurationDefault} ${tokens.transitionEasingDefault}`,
    }),
    align === 'end' && css({ marginLeft: tokens.spacingM }),
    align === 'start' && css({ marginRight: tokens.spacingM }),
    isExpanded &&
      css({
        transform: 'rotate(180deg)',
      }),
  );

export const getAccordionHeaderStyles = (props: StyleProps) => ({
  accordionHeader: getHeaderStyles(props),
  accordionHeaderIcon: getIconStyles(props),
});
import React, { FC } from 'react';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import { Collapse } from '@contentful/f36-collapse';

import { getAccordionPanelStyles } from './AccordionPanel.styles';

export interface AccordionPanelProps extends CommonProps {
  /**
   * Child nodes to be rendered in the component
   */
  children?: React.ReactNode;
  /**
   * A boolean that tells if the accordion should be expanded or collapsed
   */
  isExpanded: boolean;
  /**
   * An unique id that is necessary for the aria roles and properties
   */
  ariaId: string;
}

export const AccordionPanel: FC<ExpandProps<AccordionPanelProps>> = ({
  children,
  isExpanded = false,
  ariaId,
  testId = 'cf-ui-accordion-panel',
  ...otherProps
}: AccordionPanelProps) => {
  const styles = getAccordionPanelStyles();

  return (
    <Collapse
      data-test-id={testId}
      aria-labelledby={`accordion--${ariaId}`}
      isExpanded={isExpanded}
      {...otherProps}
    >
      <div className={styles.accordionPanelContent}>{children}</div>
    </Collapse>
  );
};
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';

export const getAccordionPanelStyles = () => {
  return {
    accordionPanelContent: css({
      width: '100%',
      padding: `${tokens.spacingXs} ${tokens.spacingM} ${tokens.spacingM}`,
    }),
  };
};
import { css, cx } from 'emotion';
import tokens from '@contentful/f36-tokens';

export const getAccordionItemStyles = ({
  className,
}: {
  className?: string;
}) => ({
  accordionItem: cx(
    css({
      padding: '0',
      margin: '0',
      borderBottom: `1px solid ${tokens.colorElementMid}`,
      '&:first-child': {
        borderTop: `1px solid ${tokens.colorElementMid}`,
      },
    }),
    className,
  ),
});
import { Accordion as OriginalAccordion } from './Accordion';
import { AccordionItem } from './AccordionItem/AccordionItem';

type CompoundAccordion = typeof OriginalAccordion & {
  Item: typeof AccordionItem;
};

export const Accordion = OriginalAccordion as CompoundAccordion;
Accordion.Item = AccordionItem;
