import React, { useState } from 'react';
import { Button, Text } from '@contentful/f36-components';

const Sidebar = () => {
  const [disabled, setDisabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const setResult = (type = "build") => {
    let message = "There was a problem building the site. Try again later.";

    if (type === "build") {
      message = "Site build triggered.";
    }

    setLoading(false);
    setDisabled(false);
    setMessage(message);
  };

  const clickHandler = () => {
    /* Update button */

    setLoading(true);
    setDisabled(true);

    const accountId = "1c96388f497c618fe44176227e8be52f";
    const projectName = "olivia-korosak";
    const email = "graciela@alanizcreative.com";
    const branch = "staging";
    const apiKey = "26c9a612df9d83fcf7fc9371d57f4d904c4d0";

    const form = new FormData();
    form.append("branch", branch);

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
        "X-Auth-Email": email,
        "X-Auth-Key": apiKey
      }
    };

    options.body = form;

    /* Request */

    fetch(`https://contentful-deploy.alanizcreative.workers.dev/client/v4/accounts/${accountId}/pages/projects/${projectName}/deployments`, options)
      .then(res => res.json())
      .then(res => {
        console.log(res);

        if (res.success) {
          setResult("build");

          const deploymentId = res.result.id.replace("-", "");


        } else {
          setResult("error");
        }
      })
      .catch(() => {
        setResult("error");
      });
  };

  return (
    <>
      <Button
        variant="primary"
        isFullWidth
        isDisabled={disabled}
        isLoading={loading}
        onClick={clickHandler}
      >
        Build Site
      </Button>
      <div aria-live="polite">
        <Text
          fontColor="gray600"
          marginTop="spacingM"
          as="p"
        >
          {message}
        </Text>
      </div>
    </>
  );
};

export default Sidebar;
