import React from 'react';
import { Button } from '@contentful/f36-components';
import { Paragraph } from '@contentful/f36-components';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

const Sidebar = () => {
  const sdk = useSDK();

  const clickHandler = () => {
    /* Variables */

    const accountIdentifier = "account_identifier";
    const projectName = "olivia-korosak";
    const apiKey = "xUfrWqPuExVg_olVHhRJFOMOgVwkzby-pAYlLc1N";
    const branch = "staging";

    /* Data */

    const form = new FormData();
    form.append("branch", branch);

    /* Options */

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
        "X-Auth-Email": apiKey,
      }
    };

    options.body = form;

    /* Url */

    const url = `https://api.cloudflare.com/client/v4/accounts/${accountIdentifier}/pages/projects/{${projectName}}/deployments`;

    /* Request */

    fetch(url, options)
      .then(res => res.json())
      .then(res => console.log(res))
      .catch(err => console.error(err));
  };

  return (
    <Button
      variant="primary"
      isFullWidth
      onClick={clickHandler}
    >
      Build Site
    </Button>
  );
};

export default Sidebar;
