import React, { useState } from 'react';
import { Button, Paragraph } from '@contentful/f36-components';

const Sidebar = () => {
  const [disabled, setDisabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const clickHandler = () => {
    /* Update button */

    setLoading(true);
    setDisabled(true);

    /* Variables */

    const accountIdentifier = "1c96388f497c618fe44176227e8be52f";
    const projectName = "olivia-korosak";
    const apiKey = "xUfrWqPuExVg_olVHhRJFOMOgVwkzby-pAYlLc1N";
    const branch = "staging";

    /* Data */

    const form = new FormData();
    form.append("branch", branch);

    /* Options */

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
        "X-Auth-Email": apiKey,
      }
    };

    options.body = form;

    /* Url */

    const url = `https://api.cloudflare.com/client/v4/accounts/${accountIdentifier}/pages/projects/${projectName}/deployments`;

    /* Request */

    fetch(url, options)
      .then(res => res.json())
      .then(res => {
        console.log(res);
        setLoading(false);
        setDisabled(false);
        setMessage("Site successfully built!");
      })
      .catch(() => {
        setLoading(false);
        setDisabled(false);
        setMessage("There was a problem building the site. Try again later.");
      });
  };

  return (
    <>
      <Button
        variant="primary"
        isFullWidth
        isDisabled={disabled}
        isLoading={loading}
        onClick={clickHandler}
      >
        Build Site
      </Button>
      <Text aria-live="polite">{message}</Text>
    </>
  );
};

export default Sidebar;
