import React from 'react';
import { cx } from 'emotion';
import {
  Box,
  Flex,
  type EntityStatus,
  type PickUnion,
  type CommonProps,
} from '@contentful/f36-core';
import { Text } from '@contentful/f36-typography';

import { AssetIcon } from './AssetIcon/AssetIcon';
import { getAssetStyles } from './Asset.styles';
import type { AssetType } from './types';

export type AssetStatus = PickUnion<
  EntityStatus,
  'archived' | 'changed' | 'deleted' | 'draft' | 'published'
>;

export interface AssetProps extends CommonProps {
  /**
   * A `src` attribute to use for image assets
   */
  src?: string;
  /**
   * The publish status of the asset
   */
  status?: AssetStatus;
  /**
   * The title of the asset
   */
  title?: string;
  /**
   * The type of asset being represented
   */
  type?: AssetType;
}

function _Asset(
  {
    className,
    src,
    status,
    testId = 'cf-ui-asset',
    title,
    type = 'image',
    ...otherProps
  }: AssetProps,
  ref: React.Ref<any>,
) {
  const styles = getAssetStyles();
  const isImage = src && src !== '' && type === 'image';

  // Do not show image previews when publish status is archived
  const showPreview = isImage && status !== 'archived';

  return (
    <Box
      className={cx(styles.relative, className)}
      testId={testId}
      ref={ref}
      {...otherProps}
    >
      {showPreview ? (
        <>
          <Flex
            alignItems="center"
            justifyContent="center"
            className={styles.height100}
          >
            <img className={styles.image} src={src} alt={title} />
          </Flex>
          {title && (
            <div className={styles.titleContainer}>
              <Text
                className={styles.title}
                fontColor="colorWhite"
                margin="spacingS"
                isTruncated
              >
                {title}
              </Text>
            </div>
          )}
        </>
      ) : (
        <Flex
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
          paddingLeft="spacingS"
          paddingRight="spacingS"
          className={styles.height100}
        >
          <Flex
            marginBottom="spacingM"
            marginTop="spacingM"
            justifyContent="center"
          >
            <AssetIcon type={type} />
          </Flex>
          <Text isTruncated marginBottom="spacingM" fontColor="gray600">
            {title}
          </Text>
        </Flex>
      )}
    </Box>
  );
}

_Asset.displayName = 'Asset';

export const Asset = React.forwardRef(_Asset);
import React from 'react';
import {
  ArchiveIcon,
  AudioIcon,
  CodeIllustrationIcon,
  ImageIcon,
  MarkupIcon,
  PdfIcon,
  PlaintextIcon,
  RichtextIcon,
  PresentationIcon,
  SpreadsheetIcon,
  VideoIcon,
} from '@contentful/f36-icons';
import type { GeneratedIconProps } from '@contentful/f36-icon';
import type { AssetType } from '../types';

import { cx } from 'emotion';
import { getAssetIconStyles } from './AssetIcon.styles';

export interface AssetIconProps
  extends Omit<GeneratedIconProps, 'illustration' | 'ref'> {
  type?: AssetType;
}

/**
 * Renders only the Illustration that would represent this asset's type
 */
export const AssetIcon = ({
  type = 'archive',
  className,
  testId = 'cf-ui-asset-icon',
  ...otherProps
}: AssetIconProps) => {
  const styles = getAssetIconStyles();
  const props = {
    ...otherProps,
    testId,
    className: cx(styles.root, className),
  };

  switch (type) {
    case 'audio':
      return <AudioIcon {...props} />;
    case 'code':
      return <CodeIllustrationIcon {...props} />;
    case 'image':
      return <ImageIcon {...props} />;
    case 'markup':
      return <MarkupIcon {...props} />;
    case 'pdf':
      return <PdfIcon {...props} />;
    case 'plaintext':
      return <PlaintextIcon {...props} />;
    case 'presentation':
      return <PresentationIcon {...props} />;
    case 'richtext':
      return <RichtextIcon {...props} />;
    case 'spreadsheet':
      return <SpreadsheetIcon {...props} />;
    case 'video':
      return <VideoIcon {...props} />;
    case 'archive':
    default:
      return <ArchiveIcon {...props} />;
  }
};

AssetIcon.displayName = 'AssetIcon';
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';

export function getAssetIconStyles() {
  return {
    root: css({
      display: 'inline-block',
      width: '39px',
      height: '39px',
      'g, path': {
        fill: tokens.gray600,
      },
    }),
  };
}
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';

export function getAssetStyles() {
  return {
    relative: css({
      position: 'relative',
    }),
    height100: css({
      height: '100%',
    }),
    image: css({
      width: 'auto',
      maxWidth: '100%',
      maxHeight: '100%',
    }),
    titleContainer: css({
      opacity: 0,
      transition: `opacity ${tokens.transitionDurationDefault} ${tokens.transitionEasingDefault}`,
      position: 'absolute',
      bottom: 0,
      right: 0,
      left: 0,
      height: '100%',
      display: 'flex',
      overflow: 'hidden',
      alignItems: 'flex-end',
      boxSizing: 'border-box',
      background: `linear-gradient(0deg, ${tokens.gray900} 0%, transparent calc(1rem * (100 / ${tokens.fontBaseDefault})), transparent 100% )`,
      ':hover': { opacity: 1 },
    }),
    title: css({
      bottom: 0,
      left: 0,
      right: 0,
      position: 'absolute',
    }),
  };
}
export const types = {
  archive: 'archive',
  audio: 'audio',
  code: 'code',
  image: 'image',
  markup: 'markup',
  pdf: 'pdf',
  plaintext: 'plaintext',
  presentation: 'presentation',
  richtext: 'richtext',
  spreadsheet: 'spreadsheet',
  video: 'video',
};

export function isAssetType(type: string): type is AssetType {
  return Object.keys(types).includes(type);
}

export type AssetType = keyof typeof types;
