import React, { useState } from 'react';
import { Button, Text } from '@contentful/f36-components';

const Sidebar = () => {
  const [disabled, setDisabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const setResult = (type = "build") => {
    let message = "There was a problem building the site. Try again later.";

    if (type === "build") {
      message = "Site build triggered.";
    }

    if (type === "done") {
      message = "Site successfully built!";
    }

    setLoading(type === "build");
    setDisabled(type === "build");
    setMessage(message);
  };

  const clickHandler = async () => {
    /* Update button */

    setLoading(true);
    setDisabled(true);

    const accountId = "1c96388f497c618fe44176227e8be52f";
    const projectName = "olivia-korosak";
    const email = "graciela@alanizcreative.com";
    const branch = "staging";
    const apiKey = "26c9a612df9d83fcf7fc9371d57f4d904c4d0";

    const form = new FormData();
    form.append("branch", branch);

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
        "X-Auth-Email": email,
        "X-Auth-Key": apiKey
      }
    };

    options.body = form;

    console.log("FORM", options, `https://contentful-deploy.alanizcreative.workers.dev/client/v4/accounts/${accountId}/pages/projects/${projectName}/deployments`);

    /* Request */

    try {
      const response = await fetch(
        `https://contentful-deploy.alanizcreative.workers.dev/client/v4/accounts/${accountId}/pages/projects/${projectName}/deployments`, options
      );

      const json = await response.json();

      if (json.success) {
        setResult("build");

        const deploymentId = json.result.id.replace(/-/g, "");

        console.log("JSON", deploymentId, json);

        const polling = setInterval(async () => {
          try {
            const poll = await fetch(
              `https://contentful-deploy.alanizcreative.workers.dev/client/v4/accounts/${accountId}/pages/projects/${projectName}/deployments/${deploymentId}`,
              {
                method: "GET",
                headers: {
                  "Content-Type": "application/json",
                  "X-Auth-Email": email,
                  "X-Auth-Key": apiKey
                }
              }
            );

            const pollJson = await poll.json();

            console.log("POLL", pollJson);

            if (pollJson.success) {
              if (pollJson.result.latest_stage.name === "deploy") {
                clearInterval(polling);
                setResult("done");
              }
            } else {
              throw new Error("Polling error");
            }
          } catch {
            clearInterval(polling);
            setResult("error");
          }
        }, 60000);
      } else {
        throw new Error("Deploy unsuccessful");
      }
    } catch {
      setResult("error");
    }
  };

  return (
    <>
      <Button
        variant="primary"
        isFullWidth
        isDisabled={disabled}
        isLoading={loading}
        onClick={clickHandler}
      >
        Build Site
      </Button>
      <div aria-live="polite">
        <Text
          fontColor="gray600"
          marginTop="spacingM"
          as="p"
        >
          {message}
        </Text>
      </div>
    </>
  );
};

export default Sidebar;
