import React, { useState } from 'react';
import { Button, Text } from '@contentful/f36-components';

const Sidebar = () => {
  const [disabled, setDisabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const clickHandler = () => {
    /* Update button */

    setLoading(true);
    setDisabled(true);

    /* Url */

    const url = "https://api.cloudflare.com/client/v4/pages/webhooks/deploy_hooks/f9c9fdcf-cd90-45f0-b71a-cd83f4ee70db";

    /* Request */

    fetch(url, { method: "POST" })
      .then(res => res.json())
      .then(res => {
        console.log(res);
        setMessage("Site build triggered.");

        const accountIdentifier = "1c96388f497c618fe44176227e8be52f";
        const projectName = "olivia-korosak";
        const apiKey = "sq2fgIHtKgnlfmYAGgURa2YIXDSJvr_Z-iMgGp8t";

        fetch(
          `https://api.cloudflare.com/client/v4/accounts/${accountIdentifier}/pages/projects/${projectName}/deployments/b2a6c2c8-cec1-43bf-95a3-24fc3a9c5c6c`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "X-Auth-Email": apiKey
            }
          })
          .then(r => r.json())
          .then(r => console.log(r))
          .catch(() => {
            setLoading(false);
            setDisabled(false);
            setMessage("There was a problem building the site. Try again later.");
          });

        /*
          {
            "result": {
              "id": "b2a6c2c8-cec1-43bf-95a3-24fc3a9c5c6c"
            },
            "success": true,
            "errors": [],
            "messages": []
          }
        */
      })
      .catch(() => {
        setLoading(false);
        setDisabled(false);
        setMessage("There was a problem building the site. Try again later.");
      });
  };

  return (
    <>
      <Button
        variant="primary"
        isFullWidth
        isDisabled={disabled}
        isLoading={loading}
        onClick={clickHandler}
      >
        Build Site
      </Button>
      <div aria-live="polite">
        <Text
          fontColor="gray600"
          marginTop="spacingM"
          as="p"
        >
          {message}
        </Text>
      </div>
    </>
  );
};

export default Sidebar;
