import React from 'react';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import * as RadixTabs from '@radix-ui/react-tabs';

export interface TabsProps extends CommonProps {
  children?: React.ReactNode;
  defaultTab?: string;
  currentTab?: string;
  onTabChange?: (tab: string) => void;
}

export const Tabs = React.forwardRef<HTMLDivElement, ExpandProps<TabsProps>>(
  (props, ref) => {
    const {
      defaultTab,
      currentTab,
      onTabChange,
      children,
      testId = 'cf-ui-tabs',
      ...otherProps
    } = props;
    return (
      <RadixTabs.Root
        {...otherProps}
        defaultValue={defaultTab}
        value={currentTab}
        onValueChange={onTabChange}
        data-test-id={testId}
        ref={ref}
      >
        {children}
      </RadixTabs.Root>
    );
  },
);

Tabs.displayName = 'Tabs';
import React from 'react';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import * as RadixTabs from '@radix-ui/react-tabs';
import { getTabStyles } from './Tabs.styles';

export interface TabProps extends CommonProps {
  /**
   * A unique id that associates the tab with a panel.
   */
  panelId: string;
  /**
   * When true, prevents the user from interacting with the tab.
   */
  isDisabled?: boolean;
  children: React.ReactNode;
}

export const Tab = React.forwardRef<HTMLDivElement, ExpandProps<TabProps>>(
  (props, ref) => {
    const {
      children,
      testId = 'cf-ui-tab',
      panelId,
      isDisabled,
      className,
      ...otherProps
    } = props;

    const styles = getTabStyles({ className, isDisabled });
    return (
      <RadixTabs.Trigger
        disabled={isDisabled}
        value={panelId}
        data-test-id={testId}
        asChild
      >
        <div {...otherProps} className={styles.tab} ref={ref}>
          <span>{children}</span>
        </div>
      </RadixTabs.Trigger>
    );
  },
);

Tab.displayName = 'Tab';
import { css, cx } from 'emotion';
import tokens from '@contentful/f36-tokens';
import type { CSSObject } from '@emotion/serialize';

const styles = {
  tabs: {
    display: 'flex',
  } as CSSObject,
  tab: {
    borderRadius: 0,
    whiteSpace: 'nowrap',
    color: tokens.gray500,
    display: 'flex',
    height: '100%',
    minHeight: tokens.spacing2Xl,
    position: 'relative',
    padding: `0 ${tokens.spacingM}`,
    fontFamily: tokens.fontStackPrimary,
    fontWeight: tokens.fontWeightDemiBold,
    border: 'none',
    cursor: 'pointer',
    outline: 'none',
    '&:focus-visible': {
      boxShadow: tokens.glowPrimary,
    },
    '&:before': {
      content: '""',
      position: 'absolute',
      background: tokens.colorPrimary,
      opacity: '0',
      bottom: '0',
      left: '0',
      right: '0',
      height: '2px',
    },
    '&:hover:before, &:hover:focus': {
      opacity: '0.5',
    },
    '&[data-state="active"]': {
      color: tokens.colorPrimary,
      '&:before, &:hover:before, &:hover:focus': {
        opacity: '1',
      },
    },
    '> span': {
      display: 'flex',
      alignSelf: 'center',
      cursor: 'inherit',
    },
  } as CSSObject,
  tabDisabled: {
    opacity: '0.5',
    cursor: 'not-allowed',
  } as CSSObject,
  tabDividerVertical: {
    '& > *:not(:first-child)': {
      boxShadow: `inset 1px 0 0px 0px ${tokens.gray200}`,
    },
  },
  tabDividerHorizontal: {
    boxShadow: `inset 0 -2px 0px 0px ${tokens.gray200}`,
  },
};

export const getTabStyles = ({ className, isDisabled }) => ({
  tab: cx(css(styles.tab), className, {
    [css(styles.tabDisabled)]: isDisabled,
  }),
});

export const getTabsStyles = ({ className, variant }) => ({
  tabList: cx(css(styles.tabs), className, {
    [css(styles.tabDividerHorizontal)]: variant === 'horizontal-divider',
    [css(styles.tabDividerVertical)]: variant === 'vertical-divider',
  }),
});

export const getTabPanelStyles = ({ className }: { className: string }) => ({
  tabPanel: cx(
    css({
      outline: 'none',
      '&:focus-visible': {
        boxShadow: tokens.glowPrimary,
      },
    }),
    className,
  ),
});
import React from 'react';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import * as RadixTabs from '@radix-ui/react-tabs';
import type { TabsContentProps } from '@radix-ui/react-tabs';
import { getTabPanelStyles } from './Tabs.styles';

export interface TabPanelProps
  extends CommonProps,
    Pick<TabsContentProps, 'forceMount'> {
  id: string;
  children: React.ReactNode;
}

export const TabPanel = React.forwardRef<
  HTMLDivElement,
  ExpandProps<TabPanelProps>
>((props, ref) => {
  const {
    children,
    testId = 'cf-ui-tab-panel',
    id,
    className,
    forceMount,
    ...otherProps
  } = props;
  const styles = getTabPanelStyles({ className });
  return (
    <RadixTabs.Content
      data-test-id={testId}
      value={id}
      asChild
      forceMount={forceMount}
    >
      <div {...otherProps} ref={ref} className={styles.tabPanel}>
        {children}
      </div>
    </RadixTabs.Content>
  );
});
TabPanel.displayName = 'TabPanel';
import React from 'react';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import * as RadixTabs from '@radix-ui/react-tabs';
import { getTabsStyles } from './Tabs.styles';

export interface TabListProps extends CommonProps {
  /**
   * visual variant of TabList
   */
  variant?: 'default' | 'horizontal-divider' | 'vertical-divider';
  /**
   * When true, keyboard navigation will loop from last tab to first, and vice versa.
   * @default true
   */
  loop?: boolean;
  children?: React.ReactNode;
}

export const TabList = React.forwardRef<
  HTMLDivElement,
  ExpandProps<TabListProps>
>((props, ref) => {
  const {
    className,
    variant,
    children,
    testId = 'cf-ui-tab-list',
    ...otherProps
  } = props;
  const styles = getTabsStyles({
    className,
    variant,
  });
  return (
    <RadixTabs.List
      {...otherProps}
      data-test-id={testId}
      className={styles.tabList}
      ref={ref}
    >
      {children}
    </RadixTabs.List>
  );
});
TabList.displayName = 'TabList';
import { Tabs as OriginalTabs } from './Tabs';
import { Tab } from './Tab';
import { TabPanel } from './TabPanel';
import { TabList } from './TabList';

type CompoundTabs = typeof OriginalTabs & {
  Panel: typeof TabPanel;
  Tab: typeof Tab;
  List: typeof TabList;
};

export const Tabs = OriginalTabs as CompoundTabs;
Tabs.Panel = TabPanel;
Tabs.Tab = Tab;
Tabs.List = TabList;
